import React, {useState, useEffect} from "react";
import { LogoutOutlined, UploadOutlined } from '@ant-design/icons';
import { Button, Card, Image, Form, Input, Upload } from 'antd';
import { useDispatch } from "react-redux";
import { startPostProfile, startGetProfile, startUpdateProfile } from "../../actions/profileAction";
import swal from 'sweetalert'

const Profile = (props) => {
    const {Meta} = Card
    const [form] = Form.useForm()
    const [profileData, setProfileData] = useState({})
    const dispatch = useDispatch()

   
  const handleProfileData = (data) => {
    setProfileData(data)
  }

    useEffect(() => {
      dispatch(startGetProfile(handleProfileData))
    }, [dispatch])


  const dummyRequest = ({ file, onSuccess }) => {
    setTimeout(() => {
      onSuccess("ok");
    }, 0);
  };


    const handleClick = () => {
        localStorage.removeItem('exp-token')
        swal("Logged Out Successfully!")
            .then((value) => {
                props.history.push('/login')
            });
        
        
    }

    const onFinish = (values) => {
        const formData = new FormData();
        for (const name in values) {
            formData.append(name, values[name]); 
        }
        if(Object.keys(profileData).length !== 0){
          const id = profileData._id
          dispatch(startUpdateProfile(id, formData, handleProfileData))
        }else{
          dispatch(startPostProfile(formData, handleProfileData))
        }
       
    }

    return (
        <div className="profile-container" >
           
           <div className="profile-item1">
            <Card
                
                hoverable
                style={{
                width: 340,
                fontFamily: 'Lato'
                }}
                cover={<Image
                          src={Object.keys(profileData).length !== 0 ? profileData.avatar : ""}
                          fallback="data:image/png;base64,"
                  />}
            >
                <Meta title={Object.keys(profileData).length !== 0 ? `Name - ${profileData.name}` : "Name"} description={Object.keys(profileData).length !== 0 ? `Occupation - ${profileData.occupation}` : "Occupation"} />
                <Button
                    style={{marginTop: '20px'}}
                    type="primary"
                    icon={<LogoutOutlined />}
                    onClick={handleClick}
                        danger
                >Logout
                </Button>
            </Card>
            </div>

       <div className="profile-item2"> 
        <Form
          
          form={form}
          name="register"
          onFinish={onFinish}
          initialValues={{name: Object.keys(profileData).length === 0  ? "" : profileData.name, occupation: Object.keys(profileData).length !== 0 ? profileData.occupation :  ''}}
          scrollToFirstError
        >
          <Form.Item
            name="name"
            label="Name"
            rules={[
              {
                required: true,
                message: 'Name is a required field!',
              }
            ]}
          >
            <Input />
          </Form.Item>

          
          <Form.Item
            name="occupation"
            label="Occupation"
            rules={[
              {
                required: true,
                message: 'Please provide your occupation',
              },
            ]}
          >
            <Input  />
          </Form.Item>


          <Form.Item
            name="avatar"
            label="Upload profile picture"
            getValueFromEvent={({file}) => file.originFileObj}
            
          >
            <Upload accept="image/png, image/jpeg, image/jpg"
                    maxCount={1}
                    listType="picture"
                    showUploadList={true}
                    customRequest={dummyRequest}
                    >
              <Button icon={<UploadOutlined />}>Click to upload</Button>
            </Upload>
          </Form.Item>

          <Form.Item >
            {
              Object.keys(profileData).length !== 0 ? <Button type="primary" htmlType="submit"  name="edit">
              Edit Profile
          </Button> : <Button type="primary" style={{backgroundColor: 'green'}}  htmlType="submit" name="create">
                Create Profile
            </Button>
            }
          </Form.Item>
    </Form>
    </div>            
           
            </div>
    )


}

export default Profile

